workunit.code:
import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

class BPBillingRuleLookupTaxFeeController {

    @Binding
    def binding

    def searchText
    def selectedItem
    def selectedItems = [];
    def handler

    def select() {
        if( handler ) handler( selectedItems )
        return '_close' 
    }

    def remove() {
        if( selectedItem ) {
            selectedItems.remove( selectedItem )
        }
    }

    def selectAcctHandler = { acct ->
        if( ! selectedItems.find{ it.objid == acct.objid } ) {
            selectedItems.add([ objid: acct.objid, name : acct.accttitle ]);
        }
        searchText = null
        binding.refresh( 'searchText' )
        binding.refresh('selectedItem')
    }

    def lookup() {
        return InvokerUtil.lookupOpener('incomeacct:lookup', [searchText:searchText, onselect:selectAcctHandler])
    }

}
