import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class LookupBusinessListController 
{
    @Service('BPApplicationService')
    def svc;
    def selected;
    def searchText;
    def txntype;

    def onselect;

    def listHandler = [
        getRows    : { return 50; },
        getColumns : {
            return [
                new Column(name:"appno", caption:"App No."),
                new Column(name:"taxpayername", caption:"Taxpayer Name"),
                new Column(name:"tradename", caption:"Trade Name"),
                new Column(name:"businessaddress", caption:"Business Address")
            ]
        },

        fetchList : { 
            def list = [];
            if( txntype ) list = svc.lookupApplicationByTxnType( searchText, txntype );
            else list = svc.lookupApproveApplication( searchText );
            if( !list ) throw new Exception( 'There are no records found.' );
            return list;
	}
    ] as SubListModel;

    def ok(){
        if( selected ) onselect( selected );
        return '_close';
    }

    def cancel(){ return '_close' };
    void search(){ listHandler.load(); }

}
