import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;

class LookupBusinessListController 
{
    def onselect;
    def searchText;
    def list;
    def selected;

    def ok() {
        if( onselect ) onselect( selected );
        return "_close";
    }

    def listHandler = [
        getRows    : { return 50; },
        getColumns : {
            return [
                new Column(name:"txnno", caption:"Business No."),
                new Column(name:"info.tradename", caption:"Trade Name"),
                new Column(name:"info.businessaddress", caption:"Business Address")
            ]
        },

        fetchList : { 
            return list;
	},
    ] as SubListModel;

    void search(){
        list.load();
    }
}
