workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class BusinessApplication
{
    @Binding
    def binding;

    @Service('BPEnum')
    def enumSvc;

    @Service("BPApplicationService")
    def svc;

    def business = [:];
    def application = [
        docstate:'DRAFT',
        info:[taxpayername:'',taxpayeraddress:'', lobs:[]],
    ];

    def typeList;
    def modeList;

    def initAddline(){
        init();
        application.txnmode = 'ONLINE';
        application.txntype = 'ADDLOB';
        return 'pageB';
    }

    def initRetire(){
        init();
        application.txnmode = 'ONLINE';
        application.txntype = 'RETIRE';
        application.txndate = new Date();
        return 'pageB';
    }

    def initRetireline(){
        init();
        application.txnmode = 'ONLINE';
        application.txntype = 'RETIRE_LINE';
        application.txndate = new Date();
        return 'pageB';
    }

    def initCaptureNew(){
        init();
        application.txnmode = 'CAPTURE';
        application.txntype = 'NEW';   
        return 'default';
    }

    def initCaptureRenew(){
        init();
        application.txnmode = 'CAPTURE';
        application.txntype = 'NEW';   
        return 'default';
    }

    def initNew(){
        init();
        application.txnmode = 'ONLINE';
        application.txntype = 'NEW';
        application.txndate = new Date();
        return 'default';
    }

    def initRenew(){
        init();
        application.txnmode = 'ONLINE';
        application.txntype = 'RENEW';
        application.txndate = new Date();
        return 'pageB';
    }

    void init(){
        def res = enumSvc.getTypes(['applicationTypes','applicationModes']);
        typeList = res.applicationTypes;
        modeList = res.applicationModes;
    }

    def onselect = { entity ->
        if( entity ){
            application.info.taxpayername = entity.entityname;
            application.info.taxpayeraddress = entity.entityaddress;
            application.info.taxpayerid = entity.objid;
            application.taxpayerid = entity.objid;
        }
        binding.refresh();
    }

    def onbpselect = { entity ->
        if( entity ){
            business.appno = entity.appno;
            business.objid = entity.objid;
            business.tradename = entity.tradename;
            business.businessaddress = entity.businessaddress;
        }
        binding.refresh();
    }

    def next(){
        application.businessid = business.objid;
        application = svc.evaluateTxnType( application );
        return InvokerUtil.lookupOpener('bp:open', [application:application]);
    }

    public def lookupBusiness(){
        return InvokerUtil.lookupOpener( 'bp:business_list', [onselect: onbpselect, searchText:business.appno] );
    }

    public def lookupPayer(){
        return InvokerUtil.lookupOpener( 'entity:lookup', [onselect: onselect, searchText:application.info.taxpayername] );
    }
}
