workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class LobController
{
    @Binding
    def binding

    @Service("LobAttributeService")
    def svc;

    def mode = 'view';
    def searchText;
    def attribute;

    def oldValue;

    def listHandler = [
        getColumns : {
            return [
                new Column(name:'name', caption:'Attribute'),
                new Column(name:'description', caption:'Description'),
            ]
        },
        fetchList    : { return svc.getList( searchText, it ); },
    ] as SubListModel

    public void create(){ 
        attribute = [:];
        mode = 'new';
    }

    public void edit(){ 
        mode = 'edit';
        oldValue = [:];
        oldValue.putAll( attribute );
        binding.refresh();
    }

    public void cancel(){ 
        attribute?.putAll( oldValue );
        oldValue = [:];
        mode = 'view'; 
    }

    public void save(){
        if( mode == 'new' ) attribute = svc.create( attribute );
        else attribute = svc.update( attribute );
        mode = 'view';
    }

    public void delete(){
        if( MsgBox.confirm('Delete?') ) {
            svc.delete( attribute );
            attribute = [:];
            listHandler.load();
            binding.refresh();
        }
    }

    void search(){
        listHandler.load();
    }
}
