workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class ApplicationListingController
{
    @Binding
    def binding

    @Service("ReportParameterService")
    def svcParams;

    @Service('BPReportService') 
    def svc 

    @Service('DateService')
    def dateSvc

    def year 
    def docstate
    def barangay
    def reportdata

    void init() {
        def parsedate = dateSvc.parseCurrentDate()
        year = parsedate.year 
    }

    def preview() {
        generateReport()
        return 'preview' 
    }

    void generateReport() {
        reportdata = svc.getApplicationListing( barangay?.barangayid, year, docstate )
        report.viewReport()
    }

    void print(){
        generateReport()
        ReportUtil.print( report.report, true )
    }

    def report = [
        getReportName : { 'bp/applicationlisting' },
        getReportData : { return reportdata },
        getParameters : { 
            def params = svcParams.getStandardParameter( OsirisContext.env.USERNAME )
            params.STATE = docstate
            params.BARANGAYNAME = ( barangay  ? barangay.barangayname : 'ALL' )
            return params 
        }
    ] as ServerReportModel;

    def back() {
        return 'default' 
    }


    def getStatelist() {
        return ['APPROVED','PERMIT_PENDING','ACTIVE']
    }

    def getBarangaylist() {
        return svc.getBarangayList()
    }
}
