workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class BusinessComparativeListingController
{
    @Binding
    def binding

    @Service("ReportParameterService")
    def svcParams;

    @Service('BPPermitReportService') 
    def svc 

    @Service('DateService')
    def dateSvc

    def entity = [:]
    def reportdata

    void init() {
        def parsedate = dateSvc.parseCurrentDate()
        entity.iyear = parsedate.year
    }

    void generateReport() {
        reportdata = svc.getPermitList( entity )
        report.viewReport()
    }

    void print(){
        generateReport()
        ReportUtil.print( report.report, true )
    }

    def report = [
        getReportName : { return 'bp/bppermitlisting' },
        getReportData : { return reportdata },
        getParameters : { 
            def params = svcParams.getStandardParameter( OsirisContext.env.USERNAME )
            params.YEAR

            return params
        }
    ] as ServerReportModel;

    def back() {
        return 'default' 
    }

    def preview() {
        generateReport()
        return 'preview' 
    }
}
