workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class InputValuesController
{
    @Binding
    def binding

    def entity 
    def selectedInfo
    def selectedLobInfo
    def inputHandler 
    def acceptInputHandler

    def cancel() {
        return '_close'
    }

    def ok() {
        def done = false 
        if( inputHandler ) {
            done = inputHandler( entity )
        }
        if( !done ) {
            return 'default' 
        }
        else {
            if( acceptInputHandler ) acceptInputHandler( entity )
            return '_close'
        }
    }

    def getFields(){
        def fieldinfos = entity.infos;
        fieldinfos.each{
            it.datatype = it.vartype;
            it.category = it.lobname;
            it.caption = it.varname;
            it.items = it.arrayvalues;
        }

        fieldinfos.sort{ a, b -> 
            def atext = (a.lobname ? a.lobname : '-') + a.name 
            def btext = (b.lobname ? b.lobname : '-') + b.name 
            return atext <=> btext 
        }
        return fieldinfos;
    }

    public def getInfoHandler(){
        return InvokerUtil.lookupOpener('input_fields', [fields: getFields()]);
    }
}
