workunit.code:
import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osisirs2.client.*

class InputValuesController
{
    @Binding
    def binding

    def entity 
    def selectedInfo
    def selectedLobInfo
    def inputHandler 
    def acceptInputHandler

    def infoListHandler = [
        getColumns : { return [
            new Column(name:'lobname', caption:'Line of Business'),
            new Column(name:'varcaption', caption:'Variable'),
            new Column(name:'value', caption:'Value', editable:true),
        ]},
        fetchList : { return entity.infos }
    ] as SubListModel

    def cancel() {
        return '_close'
    }

    def ok() {
        if( inputHandler ) entity = inputHandler( entity )
        if( entity.infos ) {
            infoListHandler.load()
            return 'default' 
        }
        else {
            if( acceptInputHandler ) acceptInputHandler( entity )
            return '_close'
        }
    }
}
