import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;

class EntityLookupController extends LookupModel 
{
    @Service("EntityService")
    def svc;

    def onselect;
    def searchText;
    def selected;

    def ok() {
        search = null
        if( onselect ) onselect( selected )
        return select();
    }

    def getListHandler() {
        return this;
    }

    int getRows() { return 20; }

    Column[] getColumns() {
        return [
            new Column(name:"entityno", caption:"Account No.", maxWidth:90),
            new Column(name:"entityname", caption:"Account Name"),
            new Column(name:"entityaddress", caption:"Address"),
        ]
    }

    List fetchList( Map params ) { 
        def s = search
        if( !s ) s = searchText 
        def list = svc.getList(searchText, null);
        if( list ) return list;
        throw new Exception("Record not found.");
    }
}
