workunit.code:


import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.io.*;
import java.io.*;

public class FileDownloadController 
{
    @FormTitle
    def title;

    @Service('FaasAttachmentService')
    def attachmentService;

    @Binding
    def binding;

    def objid;
    def buffer = new ByteArrayOutputStream();
    def image;
    def message;

    void doDownload() {
        def info = null;
        OsirisContext.clientContext.taskManager.addTask(
            new DownloadTask(
                onExecute: { counter ->
                    def data = attachmentService.downloadAttachment( objid );
                    buffer.write( data.bytes );
                    if ( !data.eof ) {
                        message = "Downloading file (" + buffer.size()/1000 + " kb)";
                        binding.refresh("message");
                        return true;
                    }
                    image = buffer.toByteArray();
                    binding.fireNavigation('viewer');
                    return false;
        }));
    }

}

class DownloadTask extends Task {

    def onExecute;
    def onComplete;
    def counter = 0;

    public boolean accept() { return true; }

    public void execute() {
        ended = !onExecute( counter );
        counter++;
    }

}
