workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

class AssignPinController 
{
    @Service("SequenceService")
    def seqService;

    @Service("ProvinceFAASService")
    def faasService;

    @Service("Var")
    def var;

    @Binding
    def binding;


    def faas;
    int oldSeries;
    int newSeries; 
    int section = 1;
    def newPin;
    def oldPin
    def acceptHandler;
    def key;

    void init() {
        section = toInt(faas.rp.info.sectionNo, 1);    
        doInit();
    }

    void doInit() {
        key = buildKey( faas );
        oldSeries = seqService.getCurrentSeries( key );
        newSeries = oldSeries;
        newPin = buildPin( key, newSeries );
        oldPin = faas.rp.info.pin;
    }

    void setSection( int sec ) {
        section = sec;
        doInit();
        binding.refresh();
    }

    void setNewSeries( int series ) {
        if( series < oldSeries ) {
            throw new Exception("Next Series should be greater than or equal to $oldSeries.");
        }
        newSeries = series;
        newPin = buildPin( key, newSeries );
        binding.refresh();
    }

    def assign() {
        if( MsgBox.confirm("Assign New PIN No.?") ) {
            if( acceptHandler ) acceptHandler( key, section, newSeries, oldSeries );
            return "_close";
        }
    }

    def doCancel() {
        faas.rp.info.pin = oldPin;
        return "_close";
    }

    private def buildKey( faas )  {
        int sectionLen = Integer.parseInt(var.section_index_len);
        key = faas.rp.info.provCityIndex + '-' + faas.rp.info.muniDistrictIndex + '-' + faas.rp.info.brgyIndex;
        key += '-' + section.toString().padLeft( sectionLen, '0');
        return key;
    }

    private def buildPin( key, newSeries ) {
        int parcelLen = Integer.parseInt( var.parcel_index_len );
        return key + '-' + newSeries.toString().padLeft( parcelLen, '0');
    }

    def toInt( sint, defValue ) {
        try {
            return Integer.parseInt( sint );
        }
        catch ( e ) {
            return defValue;
        }   
    }


}
