workunit.code:


import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

public class TaxpayerListController 
{

    @Service("TaxpayerService")
    def svc;

    @Service("TaxpayerHtmlService")
    def htmlService;

    @FormTitle
    def title;

    def selectedItem;
    def search = [lgu:"LOON"];

    def initProvince() {
        search.lguType = "PROVINCE";
        title = "List of Provincial Taxpayers";
        return "default";
    }

    def initMunicipal() {
        search.lguType = "MUNICIPALITY";
        title="List of Municipal Taxpayers";
        return "default";
    }

    def open( item ) {
        return InvokerUtil.lookupOpener( "provtaxpayer:open", [taxpayerId:item.objid] );
    }

    def newProvinceTaxpayer() {
        return InvokerUtil.lookupOpener("provtaxpayer:new",[:]);
    }

    void doSearch() {
        listHandler.load();
    }

    def listHandler = [
        getRows    : { return 25; },
        getColumns : {
            return [
                new Column( name:"item.lgu", caption:"LGU", width:120),
                new Column( name:"item.name", caption:"Name", width:250),
                new Column( name:"item.address", caption:"Address", width:200),
            ]
        },
        fetchList  : { return svc.getList( search, it); },
        onOpenItem : { item, colname -> open( item ) },
    ] as PageListModel;

    def getHtml() {
        if( selectedItem?.item ) {
            if( ! selectedItem.item.html ) {
                selectedItem.item.html = htmlService.getHtml( selectedItem.item.objid );
            }
            return selectedItem.item.html;
        }
        return "No record or item selected...";
    }

    def doOpen() {
        return open( selectedItem.item );
    }

    def getShowOpen() {
        return search.lguType == 'PROVINCE' && selectedItem?.item != null;
    }

}
