import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import rulemgmt.*;

public class ClassificationValueHandler extends ValueHandler {

    @Binding
    def binding

    def lookup() {
        def h = { o->
            value = o;
            binding.refresh();
        }
        return InvokerUtil.lookupOpener( "value_handler:classification_lookup", [handler: h ] );
    }

    public String getDisplaytext() {
        return value ? value*.name.join(', ') : '';
    }
}
