import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class CancelTDReasonsMgmtController
{
    @Binding
    def binding

    @Service("CancelTDService")
    def svc 

    def searchText
    def selectedItem
    def params

    def create() {
        return InvokerUtil.lookupOpener('canceltd.create', [:])
    }

    def open() {
        return InvokerUtil.lookupOpener('canceltd.open', [entity:selectedItem])
    }

    void delete() {
        if( selectedItem && MsgBox.confirm('Delete item?') ) {
            svc.delete( selectedItem )
            search()
        }
    }

    void search() {
        listHandler.load()
    }

    def listHandler = [
        getRows    : { return 50; },
        getColumns : {
            return [
                new Column(name:"cancelcode", caption:"Code", minWidth:40),
                new Column(name:"canceltitle", caption:"Title", minWidth:130),
                new Column(name:"canceldesc", caption:"Description", minWidth:170),
            ];
        },
        fetchList : { return svc.getList( searchText, params ) },
    ] as SubListModel;
}
