import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class ExemptionTypesMgmtController
{
    @Binding
    def binding

    @Service("ExemptionTypesService")
    def svc 

    def searchText
    def selectedItem
    def params

    def create() {
        return InvokerUtil.lookupOpener('exempttype.create', [:])
    }

    def open() {
        return InvokerUtil.lookupOpener('exempttype.open', [entity:selectedItem])
    }

    void delete() {
        if( selectedItem && MsgBox.confirm('Delete item?') ) {
            svc.delete( selectedItem )
            search()
        }
    }

    void search() {
        listHandler.load()
    }

    def listHandler = [
        getRows    : { return 50; },
        getColumns : {
            return [
                new Column(name:"exemptcode", caption:"Code", minWidth:40),
                new Column(name:"exemptdesc", caption:"Exemption Type", minWidth:170),
                new Column(name:"orderno", caption:"Order No.", minWidth:30),
            ];
        },
        fetchList : { return svc.getList( searchText, params ) },
    ] as SubListModel;
}
