workunit.code:
import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class FAASMgmtController 
{
    @Binding 
    def binding

    @Service('FAASService')
    def svc 

    @Service('FAASHtmlService')
    def htmlSvc 

    String searchText
    def selectedItem
    List list

    def getFormActions() {
        return InvokerUtil.lookupActions('faasmgmt.actions',[faasid:selectedItem?.objid])
    }


    def create() {
        return InvokerUtil.lookupOpener('faas.create', [faas:selectedItem])
    }

    def open() {
        return InvokerUtil.lookupOpener('faas.open', [faas:selectedItem])
    }

    def viewtd(){
        return InvokerUtil.lookupOpener('assessor.reports', [faasid:selectedItem.objid])
    }

    void search() {
        list = null
        listHandler.load()
        binding.refresh('html')
    }

    def listHandler =[
        getRows    : { return 25 },
        getColumns : { return [
            new Column( name:'docstate', caption:'State', maxWidth:100 ),
            new Column( name:'txntype', caption:'Txn', maxWidth:50 ),
            new Column( name:'tdno', caption:'tdno', maxWidth:150 ),
            new Column( name:'rputype', caption:'Type', maxWidth:50 ),
            new Column( name:'fullpin', caption:'PIN' ),
        ]},
        fetchList  : {
            if( ! list  ) {
                list = svc.getList( searchText, it )
            }
            return list 
        }
    ] as PageListModel


    String getHtml() {
        return htmlSvc.getHtml( selectedItem?.objid ) 
    }


}
