workunit.code:
import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class FAASMgmtController 
{
    @Binding 
    def binding

    @Service('FAASService')
    def svc 

    @Service('FAASHtmlService')
    def htmlSvc 

    String searchText
    def selectedItem
    List list

    def invokerparam = [
        getParams :  { return [faasid:selectedItem?.objid] }
    ] as InvokerParameter

    def getFormActions() {
        return InvokerUtil.lookupActions('faasmgmt.action', invokerparam )
    }


    def create() {
        return InvokerUtil.lookupOpener('faas.create', [faas:selectedItem])
    }

    def open() {
        return InvokerUtil.lookupOpener('faas.open', [faas:selectedItem])
    }

    void search() {
        list = null
        listHandler.load()
        binding.refresh('html')
    }

    def listHandler =[
        getRows    : { return 25 },
        getColumns : { return [
            new Column( name:'docstate', caption:'State', minWidth:100),
            new Column( name:'txntype', caption:'Txn', minWidth:50),
            new Column( name:'rputype', caption:'Type', minWidth:50),
            new Column( name:'barangay', caption:'Barangay', minWidth:150),
            new Column( name:'tdno', caption:'tdno', minWidth:150),
            new Column( name:'fullpin', caption:'PIN', minWidth:150),
            new Column( name:'taxpayername', caption:'Taxpayer', minWidth:250),
            new Column( name:'cadastrallotno', caption:'Cadastral Lot No.', minWidth:100),
            new Column( name:'classcode', caption:'Class', minWidth:50),
            new Column( name:'totalareaha', caption:'Area (ha)', type:'decimal', minWidth:100, format:'#,##0.000000' ),
            new Column( name:'totalmv', caption:'Market Value', type:'decimal', minWidth:100),
            new Column( name:'totalav', caption:'Assessed Value', type:'decimal', minWidth:100),


        ]},
        fetchList  : {
            if( ! list  ) {
                list = svc.getList( searchText, it )
            }
            return list 
        }
    ] as PageListModel


    String getHtml() {
        return htmlSvc.getHtml( selectedItem?.objid ) 
    }

    boolean getShowActions() {
        if( ! selectedItem || selectedItem.objid == null ) 
            return false
        return true
    }

}
