import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class KindofBldgsMgmtController
{
    @Binding
    def binding

    @Service("KindofBuildingService")
    def svc 

    def searchText
    def selectedItem
    def params

    def create() {
        return InvokerUtil.lookupOpener('kindbldg.create', [:])
    }

    def open() {
        return InvokerUtil.lookupOpener('kindbldg.open', [entity:selectedItem])
    }

    void delete() {
        if( selectedItem && MsgBox.confirm('Delete item?') ) {
            svc.delete( selectedItem )
            search()
        }
    }

    void search() {
        listHandler.load()
    }

    def listHandler = [
        getRows    : { return 50; },
        getColumns : {
            return [
                new Column(name:"bldgcode", caption:"Code", minWidth:50),
                new Column(name:"bldgkind", caption:"Title", minWidth:200)
            ];
        },
        fetchList : { return svc.getList( searchText, params ) },
    ] as SubListModel;
}
