workunit.code:



import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*

class BldgKindLookupController extends LookupModel 
{
    @Service("BldgRPUService")
    def svc

    def onselect
    def searchText
    def bldgtypeid 
    def selected

    void init(){
    }


    def ok() {
        search = null
        if( onselect ) onselect( selected )
        return select()
    }

    def getListHandler() {
        return this
    }

    int getRows() { return 25 }

    Column[] getColumns() {
        return [
            new Column(name:"bldgkindcode", caption:"Code", maxWidth:80),
            new Column(name:"bldgkindname", caption:"Kind of Building"),
        ]
    }

    List fetchList( Map params ) { 
        def s = search
        if( !s ) s = searchText 
        def list = svc.lookupBldgKind( bldgtypeid , s, params)
        if( list ) return list
        throw new Exception("Record not found.")
    }
}
