import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;

class LookupMachinesController extends LookupModel 
{
    @Service("MachinesService")
    def svc;

    def onselect;
    def searchText;
    def selected;

    void init() {;}

     def ok() {
        search = null
        if( onselect ) onselect( selected )
        return select();
    }

    def getListHandler() {
        return this;
    }

    int getRows() { return 20; }

    Column[] getColumns() {
        return [
            new Column(name:"machinecode", caption:"Code", width:70),
            new Column(name:"machinedesc", caption:"Structure", width:250),
        ]
    }

    List fetchList( Map params ) { 
        def s = search
        if( !s ) s = searchText 
        def list = svc.getList( s, params);
        if( list ) return list;
        throw new Exception("Record not found.");
    }
}
