import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class MachRYSettingMgmtController
{
    @Binding
    def binding

    @Service('MachRYSettingService')
    def svc 

    def searchText
    def selectedItem

    def create() {
        return InvokerUtil.lookupOpener('machrysetting.create', null)
    }

    def open() {
        return InvokerUtil.lookupOpener('machrysetting.open', [entity:selectedItem])
    }

    void delete() {
        if( selectedItem && MsgBox.confirm('Delete setting?') ) {
            svc.remove( selectedItem.objid )
            search()
        }
    }

    void search() {
        listHandler.load()
    }

    def listHandler = [
        getRows    : { return 50 },
        getColumns : { return [
            new Column(name:'ry', caption:'Revision Year', format:'0000', alignment:'left' ),
        ]},
        fetchList  : { return svc.getList( searchText ) },
    ] as SubListModel
}
