workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class ComparativeAVController
{
    @Service("ReportParameterService")
    def svcParams;

    @Service('RPTReportService') 
    def svc 

    @Service('DateService')
    def dateSvc

    def year
    def qtr
    def month


    def data

    void init() {
        year = dateSvc.serverYear 
    }

    def preview() {
        generateReport()
        return 'preview' 
    }

    void generateReport() {
        data = svc.generateComparativeAV( year, qtr, month?.month )
        report.viewReport()
    }

    void print(){
        generateReport()
        ReportUtil.print( report.report, true )
    }

    def report = [
        getReportName : { 'comparativeav' },
        getReportData : { return data.reportdata },
        getParameters : { svcParams.getStandardParameter( OsirisContext.env.USERNAME ) + [
                            TITLE       : data.title, 
                            PERIOD      : data.period,
                            PERIODTYPE  : data.periodtype,
                        ]}
    ] as ServerReportModel;

    def back() {
        return 'default' 
    }

    List getQuarterlist() {
        return [1,2,3,4]
    }

    List getMonthlist() {
        return svc.getMonthList( qtr )
    }

}
