import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import java.rmi.server.UID;

public class RequirementsController  extends etracs2.groovy.CRUDController  {
    @Service("RequirementsService")
    def svc;

    def getService() { return svc; }
    def getCreateFocusComponent() { return "entity.reqcode"; }
    def getEditFocusComponent() { return "entity.reqcode"; }
    def getListHandler() { return listHandler; }


    def createEntity() {
        return [:];
    }

    def listHandler = [
        getRows    : { return 20; },
        getColumns : {
            return [
                new Column(name:"reqcode", caption:"Code", minWidth:40),
                new Column(name:"reqname", caption:"Name", minWidth:170),
            ];
        },
        fetchList : { 
            return list; 
        },
    ] as SubListModel;
}
