workunit.code:
import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

class RPTBillingActionSefController
{
    @Binding
    def binding

    @Service('RPTBillingRuleService')
    def svc 

    def action
    def varlist 
    def selectedVariable
    def title = 'Calculate SEF Tax'
    def accttitle 
    def rule

    void create() {
        action = [
            opener    : 'rptbillingrule_action_sef',
            acctid    : null,
            accttitle : null, 
            expr      : null,
        ]
    }

    void open() {
       accttitle = action?.accttitle
    }

    //must be implemented by a handler
    void accept() {
        if( ! action.acctid ) throw new Exception('Account is required.')
        if( ! action.expr ) throw new Exception('Tax Computation Expression is required.')
        action.displaytext = buildDisplayText()
        action.actiontype = 'calcSef' 
    }

    def buildDisplayText() {
        return 'Calculate ' + action.accttitle + ' using the formula: ' + action.expr 
    }

    def selectAcctHandler = { acct ->
        action.acctid = acct.objid 
        action.accttitle = acct.accttitle
        accttitle = acct.accttitle 
        binding.refresh('accttitle')
    }

    def lookupAccount() {
        return InvokerUtil.lookupOpener('rptincomeacct_sef.lookup', [searchText:accttitle, onselect:selectAcctHandler])
    }

    def onselect = { action.expr = it; }
    def getFormulaEditor(){
        return InvokerUtil.lookupOpener('formula_editor2', [formula:action.expr, variableList:varlist, onselect:onselect, showToolbar:false]);
    }

}
