workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

public class RPTCertificationsNoEmcumbranceTDNoController 
{
    @Service('ReportParameterService')
    def svcParams;

    @Service('RPTCertificationsService')
    def svc;

    @Binding 
    def binding;

    def certification = [:];
    def list = [];
    def mode = '_default';
    def tdno;

    void setTdno( o ){
        tdno = o;
        certification.putAll( o );
        certification.requestedby = o.taxpayername;
        binding.refresh("certification.*");
    }

    def getLookupFaas(){
        return InvokerUtil.lookupOpener("faas.lookup", [:] );
    }

    public void setOfficialuse( o ){
        certification.official = o;
        certification.orno = ( o ) ? 'Official Use' : '';
        certification.ordate = ( o ) ? null : certification.serverDate;
        certification.oramount = ( o ) ? 0.0 : 0.0;
        certification.stampamount = ( o ) ? 0.0 : 0.0;
    }

    public def getOfficialuse(){
        return certification.official;
    }

    def report =[
            getReportName : { "rpt/td/certification/NoEncumbranceByTDCertification" },
            getReportData : { return certification; },
            getParameters : { svcParams.getStandardParameter( OsirisContext.env.USERNAME ); }
        ] as ServerReportModel;

    public def doPreview(){
        certification.putAll( svc.fetchDataNoEncumbranceTDNo(certification) );
        report.viewReport();
        mode = '_default'
        return "preview";
    }

    public void doSave(){
        certification.putAll( svc.fetchDataNoEncumbranceTDNo(certification) );
        svc.doSave( certification );
        mode = 'certified'
    }

    public def doClose(){
        mode = '_default';
        certification = [:];
        td = null;

        return '_close';
    }
}
