workunit.code:
import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class LedgerMgmtController 
{
    @Binding 
    def binding

    @Service('RPTLedgerService')
    def svc 

    @Service('RPTLedgerHtmlService')
    def htmlSvc 

    def actionList 
    String searchText

    def docstate 
    def selectedItem
    List list


    def getFormActions() {
        if( !actionList ) {
            actionList = InvokerUtil.lookupActions('rptledgermgmt.actions',[faasid:selectedItem?.objid])
        }
        return actionList 
    }

    def open() {
        return InvokerUtil.lookupOpener('rptledger.open', [ledger:selectedItem])
    }


    void search() {
        list = null
        listHandler.load()
        binding.refresh('html')
    }

    def listHandler =[
        getRows    : { return 25 },
        getColumns : { return [
            new Column( name:'docstate', caption:'State', maxWidth:100 ),
            new Column( name:'tdno', caption:'tdno', maxWidth:150 ),
            new Column( name:'fullpin', caption:'PIN', maxWidth:150 ),
            new Column( name:'taxpayername', caption:'Taxpayer' ),
        ]},
        fetchList  : {
            if( ! list  ) {
                list = svc.getList( docstate, searchText, it )
            }
            return list 
        }
    ] as PageListModel


    String getHtml() {
        return htmlSvc.getHtml( selectedItem?.objid ) 
    }

    List states 
    List getStates(){
        if( ! states ) {
            states = svc.getStates()
        }
        return states 
    }   

    void setDocstate( docstate ) {
        this.docstate = docstate
        search()
    }

}
