import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

public class RPTOfficersController  {

    @Binding
    def binding;

    @Service("RPTOfficersService")
    def svc;


    def mode = "view" ;
    def entity;

    /*---------------------------------------------------------------------
    * FormAction Support
    ---------------------------------------------------------------------*/
    def createItem() {
        mode = "create"; 
        entity = createEntity();

        return "new";
    }

    void openItem() {
        mode = "view";
        entity = svc.open( entity.objid );
    }


    /*---------------------------------------------------------------------
    * Main
    ---------------------------------------------------------------------*/

    def getOfficers(){
        return [ 'APPRAISER', 'APPROVER', 'RECOMMENDER', 'TAXMAPPER' ];
    }

    def createEntity() {
        return [:];
    }

    void save() {
        if( mode == "create") entity = svc.create( entity );
        else entity = svc.update( entity );

        mode = "view";
        binding.refresh("entity.*");
    }

    void edit() {
        mode = "edit";
        focus( "entity.lastname" );
    }

    /*---------------------------------------------------------------------
    * Support Methods
    ---------------------------------------------------------------------*/

    void focus( name ) {
        binding.refresh("entity.*");
        binding.focus( name );
    }

}
