import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class RPTOfficersMgmtController
{
    @Binding
    def binding

    @Service("RPTOfficersService")
    def svc 

    def searchText
    def selectedItem
    def params

    def create() {
        return InvokerUtil.lookupOpener('rptofficer.create', [:])
    }

    def open() {
        return InvokerUtil.lookupOpener('rptofficer.open', [entity:selectedItem])
    }

    void delete() {
        if( selectedItem && MsgBox.confirm('Delete item?') ) {
            svc.delete( selectedItem )
            search()
        }
    }

    void search() {
        listHandler.load()
    }

    def listHandler = [
        getRows    : { return 50; },
        getColumns : {
            return [
                new Column(name:"fullname", caption:"Name", minWidth:170),
                new Column(name:"position", caption:"Position", minWidth:50),
                new Column(name:"officertype", caption:"Type", minWidth:50),
            ];
        },
        fetchList : { return svc.getList( searchText, params ) },
    ] as SubListModel;
}
