workunit.code:


import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import java.rmi.server.UID;

public class RPTParametersController  {

    @Binding
    def binding;

    @Service("RPTParametersService")
    def svc;

    def mode = "view" ;
    def entity;
    def datatype;
    def paramname;

    /*---------------------------------------------------------------------
    * FormAction Support
    ---------------------------------------------------------------------*/
    def createItem() {
        mode = "create"; 
        entity = createEntity();

        return "new";
    }

    void openItem() {
        mode = "view";
        entity = svc.open( entity.objid );
    }


    /*---------------------------------------------------------------------
    * Main
    ---------------------------------------------------------------------*/

    def getParamname(){
        return entity.paramname;
    }

    void setParamname( paramname ){
        entity.paramname = trimSpecialChar(paramname);
    }

    def getDataTypes(){
        return ['decimal', 'integer', 'range_decimal', 'range_integer'];
    }


    def createEntity() {
        return [
            paramtype   : 'decimal',
            parammin    : 0,
            parammax    : 0
        ];
    }

    void save() {
        if( mode == "create") entity = svc.create( entity );
        else entity = svc.update( entity );

        mode = "view";
        binding.refresh("entity.*");
    }

    void edit() {
        mode = "edit";
        focus( "paramname" );
    }

     /*---------------------------------------------------------------------
    * Support Methods
    ---------------------------------------------------------------------*/

    void focus( name ) {
        binding.refresh("entity.*");
        binding.focus( name );
    }

    def trimSpecialChar( str ){
        String result = "";
        if( str ){
            result = str.replaceAll("\\W", "");
        }

        return result;
    }
}
