import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*

public class RPTSettingMgmtController
{
    @Binding
    def binding

    @Service("RPTSettingService")
    def svc 

    def flag = true;
    def searchText
    def selectedItem
    def params

    def create() {
        return InvokerUtil.lookupOpener('rptsetting.create', [:])
    }

    def open() {
        return InvokerUtil.lookupOpener('rptsetting.open', [entity:selectedItem])
    }

    void delete() {
        if( selectedItem && MsgBox.confirm('Delete item?') ) {
            svc.delete( selectedItem )
            search()
        }
    }

    void search() {
        listHandler.load()
    }

    def listHandler = [
        getRows    : { return 50; },
        getColumns : {
            return [
                new Column(name:"ry", caption:"Revision Year"),
                new Column(name:"ordinanceno", caption:"Ordinance No."),
                new Column(name:"ordinancedate", caption:"Ordinance Date"),
            ];
        },
        fetchList : { 
            def list = svc.getList( searchText );
            if( !list ) flag = false;
            return list;
        },
    ] as SubListModel;
}
