import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;

class LookupBankController 
{
    @Service("BankiService")
    def svc;

    def onselect;
    def searchText;
    def selected;

    def ok() {
        if( onselect ) {
            onselect( selected; );
        }
        return "_close";
    }

    def listHandler = [
        getRows    : { return 20; },
        getColumns : {
            return [
                new Column(name:"bankcode", caption:"Bank Code"),
                new Column(name:"bankname", caption:"Bank Name"),
                new Column(name:"branchname", caption:"Branch Name"),
            ];
        },
        fetchList : { 
            def list = svc.getList(searchText, null);
            if(list) return list;

            throw new Exception("Record not found.");
        }
    ] as PageListModel;
}
