import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;

class LookupRIVController 
{
    @Service("RIVService")
    def svc;

    def onselect;
    def searchText;
    def selected;

    void init() {
    }

    def ok() {
        if( onselect ) {
            onselect( selected );
        }
        return "_close";
    }

    def listHandler = [
        getRows    : { return 25; },
        getColumns : {
            return [
                new Column(name:"txnno", caption:"RIV No.", width:200),
                new Column(name:"requestedby", caption:"Requested By", width:250),
                new Column(name:"rivtype", caption:"Type", width:150),
            ]
        },
        fetchList : { 
                def search = [searchText:searchText];
                def list = svc.getListByState(search, it);
                if( list ) return list;
                throw new Exception("Record not found.");
	},
    ] as PageListModel;
}
