workunit.code:



import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;

class LookupAccountController 
{
    @Service("AccountService")
    def svc;

    def onselect;
    def searchText;
    def selected;
    def chartType;
    def parentid;

    void initSubNgas() {
        chartType = 'NGAS';
    }

    void initSubSre() {
        chartType = 'SRE';
    }

    def ok() {
        if( onselect ) {
            onselect( selected );
        }
        return "_close";
    }

    def listHandler = [
        getRows    : { return 20; },
        getColumns : {
            return [
                new Column(name:"acctcode", caption:"Code", width:70),
                new Column(name:"accttitle", caption:"Title", width:250),
            ]
        },
        fetchList : { 
                def list = svc.getSubAccountList(searchText, chartType, parentid, it);
                if( list ) return list;
                throw new Exception("Record not found.");
	},
    ] as PageListModel;
}
