workunit.code:


import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import java.rmi.server.UID;

class RemittanceReportFundDetailedController {

    @Service("RemittanceReportService")
    def svc;

    @Service("ReportParameterService")
    def paramSvc;

    @Service('FundService')
    def fundsvc;

    def entity;

    def mode;

    def selectedfund;

    def report = [
        getReportName : { return 'remittance/RemittanceByFundDetailed' },
        getReportData : { return svc.generateReportByFundDetailed( entity, selectedfund ) }, 
        getParameters : { 
            def params = paramSvc.getStandardParameter( OsirisContext.env.USERNAME );
            if (selectedfund)
                params.SUBTITLE = selectedfund.fundname;
            return params;
        }
    ] as ServerReportModel;

    def close() {
        return '_close';
    }

    def view() {
        mode = 'init';
        return 'init';
    }

    def viewReport() {
        mode = 'view';
        report.viewReport();
        return 'report';
    }

    def getFundList() {
        return fundsvc.getList( null, null );
    } 

}
