workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import java.rmi.server.UID;

class RemittanceReportSummaryOfCollectionController {

    @Service("RemittanceReportService")
    def svc;

    @Service("ReportParameterService")
    def paramSvc;

    def entity;

    def mode;
    def reportdata 
    def chart 

    def report = [
        getReportName : { return 'remittance/RCDSummaryOfCollection' },
        getReportData : { return reportdata.items  }, 
        getParameters : { 
            def params = paramSvc.getStandardParameter( OsirisContext.env.USERNAME );
            params.putAll( reportdata.header )
            params.REPORTTITLE = 'SUMMARY OF COLLECTION BY ' + chart 
            params.REMITTANCENO = entity.info.txnno
            params.REMITTANCEDATE = entity.info.txndate
            params.COLLECTORNAME = entity.info.collectorname
            params.COLLECTORTITLE = entity.info.collectortitle
            params.LIQUIDATINGOFFICER = entity.info.liquidatingofficername
            params.LIQUIDATINGOFFICERTITLE = entity.info.liquidatingofficertitle
            return params
        }
    ] as ServerReportModel;

    def close() {
        mode = 'init';
        return '_close';
    }

    void viewNGAS() {
        chart = 'NGAS'
        view()
    }

    void viewSRE() {
        chart = 'SRE'
        view()
    }

    void view() {
        reportdata = svc.generateSummaryOfCollection( entity.objid, chart )
        report.viewReport();
        mode = 'init';
    }

}
