workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;

public class RemittanceListController {

    @Service("RemittanceService")
    def svc;

    def entity;

    def searchText;

    def list;

    def selectedItem;

    @Binding
    def binding;

    def listHandler = [

        getRows    : { return 50; },

        getColumns : {
            return [
                new Column( name:"txnno", caption:"Rem. No.", minWidth:60 ),
                new Column( name:"collectorname", caption:"Collector", width:350 ),
                new Column( name:"txndate", caption:"Date", format: "yyyy-MM-dd", width:100 ),
                new Column( name:"amount", caption:"Amount", format: "#,##0.00", width:140 )
            ];
        },

        fetchList : { 
            return svc.getList( searchText, it );
        }

    ] as PageListModel;

    void search() {
        listHandler.load();
    }

    def create() {
        return new WindowOpener( name:"tc2:remittance", action:"create" );
    }

    def open() {
        return new WindowOpener( name:"tc2:remittance", action:"open", params:[entity:selectedItem] );
    }

    def getHtml() {
        return svc.getHtml( svc.open(selectedItem?.objid) );
    }



}
