import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import java.rmi.server.UID;

class AbstractOfCollectionReportController {

    def reports;

    def selectedreport;

    def getReportList() {
        if( !reports ) reports = InvokerUtil.lookupOpeners("abstractofcollection:report", [mode:'init']);
        return reports;
    } 

    def viewReport() {
        if (!selectedreport) return null;
        return selectedreport;
    }

    def close() {
        return '_close';
    }

}
