workunit.code:


import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import java.rmi.server.UID;

class AbstractOfCollectionReportDailyController {

    @Service("TCReportService")
    def svc;

    @Service("ReportParameterService")
    def paramsvc;

    @Service("DateService")
    def dtsvc;

    def collectiondate;

    def mode = 'init';

    def report = [
        getReportName : { return 'AbstractOfCollection' },
        getReportData : { return svc.generateAbstractOfCollectionDaily( collectiondate ) }, 
        getParameters : { 
            def params = paramsvc.getStandardParameter( OsirisContext.env.USERNAME );
            params.SUBTITLE = "For " + collectiondate;
            return params;
        }
    ] as ServerReportModel;

    def close() {
        return '_close';
    }

    def view() {
        mode = 'init';
        collectiondate = dtsvc.getServerDate();
        return 'init';
    }

    def viewReport() {
        mode = 'view';
        report.viewReport();
        return 'report';
    }

}
