workunit.code:


import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import java.rmi.server.UID;

class AbstractOfCollectionReportQuarterlyController {

    @Service("TCReportService")
    def svc;

    @Service("ReportParameterService")
    def paramsvc;

    @Service("DateService")
    def dtsvc;

    def param = [:];

    def mode = 'init';

    def qtrs;

    def report = [
        getReportName : { return 'AbstractOfCollection' },
        getReportData : { return svc.generateAbstractOfCollectionQuarterly( param ) }, 
        getParameters : { 
            def params = paramsvc.getStandardParameter( OsirisContext.env.USERNAME );
            params.SUBTITLE = "For " + param.qtr.caption + ", " + param.year;
            return params;
        }
    ] as ServerReportModel;

    def close() {
        return '_close';
    }

    def view() {
        def map = dtsvc.parseCurrentDate();
        param.year = map.year;
        param.qtr = getQtrs().find{ qtr -> qtr.index == map.qtr };
        mode = 'init';
        return 'init';
    }

    def viewReport() {
        mode = 'view';
        report.viewReport();
        return 'report';
    }

    def getQtrs() {
        if (!qtrs) qtrs = dtsvc.getQtrs();
        return qtrs;
    }

}
