workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import java.rmi.server.UID;

class AccountListingController
{
    @Service("ReportParameterService")
    def svcParams;

    @Service("AccountListingService")
    def svc;


    def mode = "build";
    def entity = [];
    def data = [];

    def chartType;

    void init(){
        entity = [
            charttype : 'NGAS'
        ];
    }

    def getChartTypeList(){
        return [ 'NGAS', 'SRE' ];
    }

    void setChartType( chartType ){
        entity.charttype = chartType;
    }

    def view() {
       data = svc.buildReport( entity );
       if( !data ) throw new Exception("No results found.");
       report.viewReport();
       mode = 'view';
       return "preview";
    }

    def back(){
        mode = 'build';
        return "reportPage";
    }

    def report =[
        getReportName : { "accounting/account" },
        getReportData : { return data; },
        getParameters : { getStandardReportParams(); }
    ] as ServerReportModel;


    def getStandardReportParams() {
        return svcParams.getStandardParameter( OsirisContext.env.USERNAME ) + [
            charttype   : entity.charttype
        ]; 
    }

}
