import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class IncomeAcctListingController extends etracs2.groovy.ReportController
{
    @Service("Var")
    def var;

    @Service("DateService")
    def dateSvc;

    @Service("IncomeAcctListingService")
    def svc;

    def getVarService() { return var; }
    def getDateService() { return dateSvc; }
    def getService() { return svc; }
    def chartType;

    void init(){
        entity = [
            charttype : 'NGAS'
        ];
    }

    def getChartTypeList(){
        return [ 'NGAS', 'SRE' ];
    }

    void setChartType( chartType ){
        entity.charttype = chartType;
    }

    public String reportName(){
        return "etracs2/tc/report/IncomeAcctListingwChart.jasper";
    }

    def getStandardReportParams() {  
        return super.standardReportParams + [
            charttype   : entity.charttype
        ]; 
    }

}
