workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class IncomeAcctListingController
{   
    @Service("ReportParameterService")
    def svcParams;

    @Service("IncomeAcctListingService")
    def svc;

    def mode = "build";
    def entity = [];
    def data = [];

    def chartType;

    void init(){
        entity = [
            charttype : 'NGAS'
        ];
    }

    def getChartTypeList(){
        return [ 'NGAS', 'SRE' ];
    }

    void setChartType( chartType ){
        entity.charttype = chartType;
    }

    def view() {
       data = svc.buildReport( entity );
       if( !data ) throw new Exception("No results found.");
       report.viewReport();
       mode = 'view';
       return "preview";
    }

    def back(){
        mode = 'build';
        return "reportPage";
    }

    def report =[
        getReportName : { "incomeaccount/incomeacct" },
        getReportData : { return data; },
        getParameters : { getStandardParameter(); }
    ] as ServerReportModel;


    def getStandardParameter(){
        return svcParams.getStandardParameter( OsirisContext.env.USERNAME ) + [ 
            charttype   : entity.charttype
        ]; 
    }
}
