workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;
import java.rmi.server.UID;

class StatementOfRevenue
{
    def mode = "build";
    @Service("ReportParameterService")
    def svcParams;

    @Service("TreasuryReportService")
    def svc;

    def entity = [:];
    def qtrList = [1,2,3,4];
    def monthList =  [
            [name:"JANUARY", value:1],
            [name:"FEBRUARY", value:2],
            [name:"MARCH", value:3],
            [name:"APRIL", value:4],
            [name:"MAY", value:5],
            [name:"JUNE", value:6],
            [name:"JULY", value:7],
            [name:"AUGUST", value:8],
            [name:"SEPTEMBER", value:9],
            [name:"OCTOBER", value:10],
            [name:"NOVEMBER", value:11],
            [name:"DECEMBER", value:12]
    ];

    def data;
    def param;

    def report =[
        getReportName : { "treasury/statementofrevenueexpanded" },
        getReportData : { return data; },
        getParameters : { return param; }
    ] as ServerReportModel;

    def back(){
        mode = "build";
        return "default";
    }

    def getFundList(){
        return svc.fundList;
    }

    def view(){
        data = svc.generateStatementOfRevenueExpanded( entity.year, entity.qtr, entity.month, entity.fund.objid );
        param = svcParams.getStandardParameter( OsirisContext.env.USERNAME );
        param.put("FUND", entity.fund.fundname);
        param.put("PERIOD", entity.year );
        report.viewReport();
        mode = "view";
        return "preview";
    }
}
