import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.util.*;

public class ScheduleEntryController {

    def entry;

    def value;
    def saveHandler;

    public void init() {
        if(value) 
            entry = new TimeUtil.ScheduleBean(value);
        else
            entry = new TimeUtil.ScheduleBean();
    }

    public def doOk() {
        if(saveHandler) saveHandler( entry );
        return "_close";
    }

}
