workunit.code:

import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;

public class ChangePwdController
{            
    @Script("User")
    def user;

    def oldpwd;
    def newpwd;
    def confirmpwd;

    def changePwd() {
        user.changePwd( oldpwd, newpwd, confirmpwd );
        return "_close";    
    }
}
